  }
};
export const formatoR = async (req, res) => {
  const { entrevista, nomT, carrera, nomA, descEstrategias, fechaEntrega }=req.body;
  const newFormato = new Formato({
    entrevista,
    nomT,
    carrera,
    nomA,
    descEstrategias,
    fechaEntrega,
  });
  await newFormato.save();
  const x = "64bde90cb1dd9380f468160e";
