export const isAuthenticated = (req, res, next) => {
  if (req.isAuthenticated()) {
    return next();
  }
  //{ message: "Contraseña Incorrecta" }
  req.flash("errors_msg", "Debe iniciar sesión.");
  res.redirect("/signin");
};

